import React, {useState, useEffect} from 'react';
import { AgGridReact } from 'ag-grid-react';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';

import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-material.css';
import AddCar from './AddCar';
import EditCar from './EditCar';
import { API_URL } from '../constants';




function Carlist() {
    const [cars, setCars] = useState([]);

    const [open, setOpen] = useState(false);


    const [columnDefs] = useState([
        {field: 'brand', sortable: true, filter: true},
        {field: 'model', sortable: true, filter: true},
        {field: 'color', sortable: true, filter: true},
        {field: 'fuel', sortable: true, filter: true, width: 100},
        {field: 'year', sortable: true, filter: true, width: 100},
        {field: 'price', sortable: true, filter: true, width: 100},
        {
            cellRenderer: params => <EditCar params={params.data} />,
            width: 120
        },
        {cellRenderer: params => 
        <Button 
        size='small' 
        color='error'
        onClick={()=> deleteCar(params)}
        >
            Delete
        </Button>
        , width: 120}
    ]) 

    const deleteCar= (params) => {
       if (window.confirm('Are you sure?')) {
        fetch(params.data._links.car.href, { method: 'DELETE'})
        .then(response =>{
            if (response.ok) {
                //Do something here
                setOpen(true);
                getCars();
            }

            else {
                alert('Something went wrong in deletion');
            }
        })
        .catch(err=> console.error(err))
    }

    }

    const updateCar= (updatedCar, url) => {

         fetch(url,  {
             method: 'PUT',
             headers: {'Content-type': 'application/json'},
             body: JSON.stringify(updateCar)
            })
         .then(response =>{
             if (response.ok) {
                 getCars();
             }

             else {
                 alert('Something went wrong when edition');
             }
         })
         .catch(err=> console.error(err))
     }




    const getCars = () => {
        //Fetch cars
        fetch(API_URL + 'cars')
        .then(response => {
            if (response.ok)
                return response.json();
            else
                alert('Something went wrong in GET request')})
        .then(data => setCars(data._embedded.cars))
        .catch(err => console.error(err))
    }

    const addCar = (car) => {
        fetch(API_URL + 'cars', {
            method: 'POST',
            headers: {'Content-type': 'application/json'},
            body: JSON.stringify(car)
        })
        .then(response => {
            if (response.ok)
            //something here
                getCars();
            else
                alert('Something went wrong in addition: ' + response.statusText)
        })
        .catch(err => console.error(err))

    }

    useEffect(()=>{
        getCars();
    },[])


return(
<>
<AddCar addCar={addCar} updateCar={updatedCar}/>
<div className="ag-theme-material" style={{width: "90%", height: 600, margin: "auto"}}>
    <AgGridReact
    rowData={cars}
    columnDefs={columnDefs}
    pagination={true}
    paginationPageSize={10}

    />

</div>
<Snackbar 

open={open}
message="Car deleted successfully"
autoHideDuration={3000}
onClose={()=> setOpen(false)}

/>



</>
);
}

export default Carlist;
